INSERT INTO `tsys_config` (`type`,`ckey`,`cvalue`,`updater`,`update_datetime`,`remark`,`company_code`,`system_code`) VALUES ('sys_txt','mnemonic_ZH_CN','<p>助记词是明文私钥的另一种表现形式,&nbsp;最早是由&nbsp;BIP39&nbsp;提案提出,&nbsp;其目的是为了帮助用户记忆复杂的私钥&nbsp;(64位的哈希值)。助记词一般由12、15、18、21个单词构成,&nbsp;这些单词都取自一个固定词库,&nbsp;其生成顺序也是按照一定算法而来,&nbsp;所以用户没必要担心随便输入&nbsp;12&nbsp;个单词就会生成一个地址。虽然助记词和&nbsp;Keystore&nbsp;都可以作为私钥的另一种表现形式,&nbsp;但与&nbsp;Keystore&nbsp;不同的是,&nbsp;助记词是未经加密的私钥,&nbsp;没有任何安全性可言,&nbsp;任何人得到了你的助记词,&nbsp;可以不费吹灰之力的夺走你的资产。所以在用户在备份助记词之后,&nbsp;一定要注意三点:</p><p>&nbsp;</p><p>1.&nbsp;尽可能采用物理介质备份,&nbsp;例如用笔抄在纸上等,&nbsp;尽可能不要采用截屏或者拍照之后放在联网的设备里,&nbsp;以防被黑客窃取;</p><p>2.&nbsp;多次验证备份的助记词是否正确,&nbsp;一旦抄错一两个单词,&nbsp;那么将对后续找回正确的助记词带来巨大的困难;</p><p>3.&nbsp;将备份后的助记词妥善保管,&nbsp;做好防盗防丢措施。</p><p><br></p>','admin',now(),'什么是助记词（中文）','CD-TOKEN00018','CD-TOKEN00018');
